mapper.update:

	update board
	set title = #{title} , content = #{content}
	where no = #{bno}


	update board 
	set hit = hit + 1
	where no = #{bno}

mapper.select:

	select count(no) no
	from board 
	where title like #{keyWord}


	select count(no) no
	from board 
	where title like #{keyWord} or content like #{keyWord}


	select count(b.no) no
	from board b, users u
	where b.user_no = u.no and u.name like #{keyWord}
